import{_ as e,W as a,X as i,a1 as t}from"./framework-5afa3ebb.js";const l={},n=t('<p>ACID complicancy is a principle for database.</p><h2 id="atomicity" tabindex="-1"><a class="header-anchor" href="#atomicity" aria-hidden="true">#</a> Atomicity</h2><ul><li>All operations in a statement should be all executed or none of them are executed</li><li>If there are fails when execute transaction, it will be rollback to original state</li></ul><h2 id="consistancy" tabindex="-1"><a class="header-anchor" href="#consistancy" aria-hidden="true">#</a> Consistancy</h2><ul><li>The transaction can only modify the tables under a predefined way</li><li>If we input some wrong data, it will not affect the whole etable</li></ul><h2 id="isolation" tabindex="-1"><a class="header-anchor" href="#isolation" aria-hidden="true">#</a> Isolation</h2><ul><li>Allow concurrent operations on a table</li><li>These operations will not affect others</li></ul><h2 id="durability" tabindex="-1"><a class="header-anchor" href="#durability" aria-hidden="true">#</a> Durability</h2><ul><li>After the transaction finish modify of data, the change should be permanently</li><li>Even system fails, database will not lose data</li></ul>',9),o=[n];function r(s,c){return a(),i("div",null,o)}const d=e(l,[["render",r],["__file","ACID-compliancy.html.vue"]]);export{d as default};
